dave = {"img":'uploads/4/2/4/7/42473969/606777_orig.jpg', "thumb":'static/uploads/4/2/4/7/42473969/606777.jpg',"name":'David Eichert',"bio":"is a political scientist interested in minority rights and international gender issues. While at Harvard's Berkman Center for Internet & Society, he worked under Professor Jonathan Zittrain doing research on the Internet's potential to create new political identities; he has also spoken to the UN Human Rights Council about refugee issues. David graduated from Brigham Young University with University Honors."}
dennis = {"img":'uploads/4/2/4/7/42473969/7605009_orig.jpg', "thumb":'static/uploads/4/2/4/7/42473969/7605009.jpg',"name":'Dennis Redeker',"bio":"is a graduate student of international politics at the University of Bremen, Germany. He holds a BA in Politics, Philosophy and Economics from University College Maastricht. His professional background combines climate change adaptation policies and digital monitoring tools. In the past, he conducted field research in the Middle East and the South Caucasus."}
nick = {"img":'uploads/4/2/4/7/42473969/7354161_orig.png', "thumb":'static/uploads/4/2/4/7/42473969/7354161.png',"name":'Nick Rubin',"bio":"is a 17-year-old high school student who is passionate about technology and coding for social change. A self-taught programmer and activist, he recently launched a project called Greenhouse (http://allaregreen.us/) that exposes the role that money plays in US Congress via web browser extension. Greenhouse now has over 150,000 users and has been featured in publications worldwide."}
ingmar = {"img":'uploads/4/2/4/7/42473969/_8867810.jpg', "thumb":'static/uploads/4/2/4/7/42473969/_8867810.jpg',"name":'Ingmar Sturm',"bio":"received his B.A. in Social Sciences and Philosophy with honors from University College Maastricht and is now a graduate student of International Relations at the University of Bremen. He conducted field research in Jordan which added to his interest in migration. Currently he is devoting his time to teaching refugees with Teach First Deutschland in Bremen."}
blaine = {"img":'uploads/4/2/4/7/42473969/_5150535564_orig.jpg', "thumb":'static/uploads/4/2/4/7/42473969/_5150535564.jpg',"name":'Blaine McGary',"bio":"is a student at Brigham Young University in the United States studying computer science. He is currently studying Software Design and Architecture and Computer Security. He has worked in various capacities using computer technology to resolve difficult industry problems throughout the United States. In his free time Blaine enjoys creating Android apps, websites, playing Ultimate Frisbee and eating Mexican food!"}
tess = {"img":'uploads/4/2/4/7/42473969/DSC02211_orig.JPG', "thumb":'static/uploads/4/2/4/7/42473969/dsc02211.jpg', "name":"Tess McLoud", "bio":"is a Masterâ€™s student at Middlebury College in Paris working on a thesis about climate change politics in the Pacific while interning at the Delegation of Palau to UNESCO. She served two years as a community development volunteer with the US Peace Corps in Chiang Rai, Thailand, and holds a BA cum laude in Anthropology and French from Dartmouth College." }
sanya = {"img":'uploads/4/2/4/7/42473969/831288990312023_orig.jpeg', "thumb":'static/uploads/4/2/4/7/42473969/831288990312023.jpg', "name":"Sanya Shahrasbi", "bio":"is an intern at the Delegation of Palau to UNESCO in Paris, while preparing to return to school soon. She holds a B.A. in Integrative Biology and International Area Studies from the University of California, Berkeley. She has extensive work experience in molecular cloning of proteins involved in the photosynthetic chain as an undergraduate researcher at the Niyogi Lab at Berkeley."}
