# -*- coding: utf-8 -*-
dave = {"img":'uploads/4/2/4/7/42473969/606777_orig.jpg', "thumb":'static/uploads/4/2/4/7/42473969/606777.jpg',"name":'David Eichert',"bio":"is a political scientist interested in minority rights and international gender issues. While at Harvard's Berkman Center for Internet & Society, he worked under Professor Jonathan Zittrain doing research on the Internet's potential to create new political identities; he has also spoken to the UN Human Rights Council about refugee issues. David graduated from Brigham Young University with University Honors."}
dennis = {"img":'uploads/4/2/4/7/42473969/7605009_orig.jpg', "thumb":'static/uploads/4/2/4/7/42473969/7605009.jpg',"name":'Dennis Redeker',"bio":"is a graduate student of international politics at the University of Bremen, Germany. He holds a BA in Politics, Philosophy and Economics from University College Maastricht. His professional background combines climate change adaptation policies and digital monitoring tools. In the past, he conducted field research in the Middle East and the South Caucasus."}
ingmar = {"img":'uploads/4/2/4/7/42473969/_8867810.jpg', "thumb":'static/uploads/4/2/4/7/42473969/_8867810.jpg',"name":'Ingmar Sturm',"bio":"received his B.A. in Social Sciences and Philosophy with honors from University College Maastricht and is now a graduate student of International Relations at the University of Bremen. He conducted field research in Jordan which added to his interest in migration. Currently he is devoting his time to teaching refugees with Teach First Deutschland in Bremen."}
blaine = {"img":'uploads/4/2/4/7/42473969/_5150535564_orig.jpg', "thumb":'static/uploads/4/2/4/7/42473969/_5150535564.jpg',"name":'Blaine McGary',"bio":"is a graduate of Brigham Young University in the United States, where he studied computer science, software design, and computer security. He has worked in various capacities using computer technology to resolve difficult industry problems throughout the United States and currently resides in Washington state. In his free time Blaine enjoys creating Android apps, websites, playing Ultimate Frisbee and eating Mexican food!"}
sanya = {"img":'uploads/4/2/4/7/42473969/831288990312023_orig.jpeg', "thumb":'static/uploads/4/2/4/7/42473969/831288990312023.jpg', "name":"Sanya Shahrasbi", "bio":"joined the team while interning at the Delegation of Palau to UNESCO in Paris. She holds a B.A. in Integrative Biology and International Area Studies from the University of California, Berkeley. She has extensive work experience in molecular cloning of proteins involved in the photosynthetic chain as an undergraduate researcher at the Niyogi Lab at Berkeley."}
joey = {"img":'uploads/4/2/4/7/42473969/628A8562.jpg', "thumb":'static/uploads/4/2/4/7/42473969/628A8562.jpg', "name":"Joey Diaz", "bio":"Joey Diaz is a student at Miami Dade College pursuing a Bachelor in Computer Information Systems with a concentration in Mobile Applications. Programming and continuously learning new technologies are his passions. He contributed in a development of a desktop application that enabled the management of an inventory. Currently, he is working in the development of an Android application part of a big project called the Island Ark Project as well as the deployment of a web site to aid in business growth for companies in need of these services around the Miami area."}
eric = {"img":'uploads/4/2/4/7/42473969/eric.jpg', "thumb":'static/uploads/4/2/4/7/42473969/eric.jpg', "name":"Eric Pollard", "bio":"is a software developer from the Northeastern United States who is passionate about global issues, especially relating to conservation and the environment."}
